    private Pattern pattern;
    private Matcher matcher;

    @Override
    public Set<ValidationError> validate(Object obj) {
        Set<ValidationError> errors = new LinkedHashSet<>();
        String email = (String) obj;
        if (!(email != null && email.isEmpty())) {
            pattern = Pattern.compile(EMAIL_PATTERN);
            matcher = pattern.matcher(email);
            if (!matcher.matches()) {
                errors.add(new ValidationError("email", "Enter correct email"));
            }
            if (email.length() < 10 || email.length() > 60) {
                errors.add(new ValidationError("email", "Email length should be between 10 and 60 "));
            }
        } else {
            errors.add(new ValidationError("email", "Email shouldn't be empty"));
        }
        return errors;
    }

